let _racePosition = toscalar(Gold_RaceTelemetry_Deduped
| where SessionId == _sessionId
| where ChannelId == 'RacePosition'
| extend value = toint(parse_json(Data)[0])
| project value);
//
let _lapNumber = toscalar(Gold_RaceTelemetry_Deduped
| where SessionId == _sessionId
| where ChannelId == 'LapNumber'
| extend value = toint(parse_json(Data)[0])
| project value);
//
let _lapStats = Silver_RaceTelemetry
| summarize arg_max(tolong(Timespan.StartTS), *) by ChannelId, CarId, SessionId, LapId
| where SessionId == _sessionId
| where ChannelId == 'CurrentLap'
| extend Time = round(todecimal(parse_json(Data)[0]), 4)
| order by LapId asc   
| project Lap = LapId + 1,
  Time,
  Diff = round(Time - prev(Time, 1), 4),
  PrevLapTime = prev(Time, 1);
//
let _currentLapTime = toscalar(_lapStats 
| order by Lap desc 
| take 1
| project Time);
//
let _lastLapTime = toscalar(_lapStats 
| order by Lap desc 
| take 1
| project PrevLapTime);
//
let _currentLapDiff = toscalar(_lapStats 
| order by Lap desc 
| take 1
| project Diff);
//
let _bestLapTime = toscalar(_lapStats 
| order by Time asc 
| take 1
| project Time);
