let _raceDetail = Gold_RaceTelemetry_Deduped
| where SessionId == _sessionId
| take 1
| project SessionId,     
    CarId, 
    Position = tostring(_racePosition), 
    Lap = tostring(_lapNumber + 1),
    CurrentLap = format_timespan(totimespan(round(_currentLapTime, 4) * 1s), "mm:ss.sss"),     
