union 
(
    _raceDetail | project ColumnOrder = 1, ColumnName = 'CarId', Value = CarId
),
(
    _raceDetail | project ColumnOrder = 2, ColumnName = 'Position', Value = Position
),
(
    _raceDetail | project ColumnOrder = 3, ColumnName = 'Lap', Value = Lap
),
(
    _raceDetail | project ColumnOrder = 4, ColumnName = 'Current Lap', Value = CurrentLap
),
(
    _raceDetail | project ColumnOrder = 8, ColumnName = 'Time Gain', Value = TimeGain
),
(
    _raceDetail | project ColumnOrder = 7, ColumnName = 'Last Lap', Value = LastLap
),
(
    _raceDetail | project ColumnOrder = 6, ColumnName = 'Best Lap', Value = BestLap
