let _trackDetail = Silver_RaceTelemetry//Gold_LapSignal_Deduped
| where SessionId == _sessionId
| take 1
| project Track_Circuit, Track_Name, Track_Length_KM, Track_Location, Track_Country_IOC_Code;
//
let _finalTrackData = 
union 
(
    _trackDetail | project ColumnOrder = 1, ColumnName = 'Circuit', Value = Track_Circuit
),
(
    _trackDetail | project ColumnOrder = 2, ColumnName = 'Name', Value = Track_Name
),
(
    _trackDetail | project ColumnOrder = 3, ColumnName = 'Length', Value = strcat(tostring(Track_Length_KM), " KM")
),
(
    _trackDetail | project ColumnOrder = 4, ColumnName = 'Location', Value = Track_Location
),
(
    _trackDetail | project ColumnOrder = 5, ColumnName = 'Country', Value = Track_Country_IOC_Code
);
//
_finalTrackData
| order by ColumnOrder asc

Silver_RaceTelemetry
| where SessionId == _sessionId
| where ChannelId == 'TireTempFrontLeft' or ChannelId == 'TireTempFrontRight' or ChannelId == 'TireTempRearLeft' or ChannelId == 'TireTempRearRight'
| mv-expand Data
| extend Temp = toint(parse_json(Data))
| take 50000
| order by StartDateTime
//| project ChannelId, Temp = toint(parse_json(Data)[0]), StartDateTime;
| project ChannelId, Temp, StartDateTime;

